import React, { useState } from 'react'
import './Card.scss'

function Card(props) {
    const [pressed, setPressed] = useState(false)

    const handleChange = () => {
        setPressed(!pressed)
    }
    return (
        <div className="card">
            <div className="card-body">
                <div className="card-word">{props.word}</div>
                <div className="card-transcription">{props.transcription}</div>

                {!pressed ? (
                    <button className="card-translation">Проверить</button>
                ) : (
                    <div className="card-translation">{props.translatin}</div>
                )}
            </div>
        </div>
    )
}

export default Card
