import React, {useState} from "react";
import "./Input.scss";
import ButtonDel from "../Buttons/ButtonDelete";
import ButtonAdd from "../Buttons/ButtonAdd";

function InputForWord(props) {
  const [imputData, setImputData]=useState({
word:"",
transcription:"",
translation:""
  });

  const addImputData = event =>{
     setImputData({
    ...imputData,
    [event.target.name]: event.target.value
  });
  }

    const {word, transcription, translation} = imputData;


  return (
      <tr className="rowAddWord">
      <td>

    <input 
    placeholder="Enter" 
    type="text"
    value={word} 
    onChange={e=>addImputData(e)} 
    name="word"
className={!word.length ? "" : 'red-border'}
    />
     </td>
          <td>

    <input className="addWord" 
    placeholder="Enter" 
    type="text" 
    value={transcription} 
    onChange={e=>addImputData(e)} 
    name="transcription"
    className={!transcription.length ? "addWord" : 'red-border'}
    />
     </td>
          <td>

   <input  
   placeholder="Enter"  
   type="text" 
   value={translation} 
   onChange={e=>addImputData(e)} 
   name="translation" 
   className={!translation.length ? "addWord"  : 'red-border'}
   />
     </td>
       <td>
        <div className="buttons">
        <ButtonAdd

        />
        <ButtonDel />
        </div>
      </td>
        </tr>
  );
}

export default InputForWord;
