import React, { useState } from "react";
import ButtonDel from "../Buttons/ButtonDelete";
import ButtonAdd from "../Buttons/ButtonAdd";
import ButtonEdit from "../Buttons/ButtonEdit";
import "./TableRow.scss";

function TableRow(props) {
  const [pressed, setPressed] = useState(false);
  const handleChange = () => {
    setPressed(!pressed);
  };

  return (
    <tr className="row" key={props.id}>
      {pressed ? (
        <div onClick={handleChange}>
          <td>
            <input className="row-input" defaultValue={props.word} />
          </td>
          <td>
            <input className="row-input" defaultValue={props.transcription} />
          </td>
          <td>
            <input className="row-input" defaultValue={props.translation} />
          </td>
        </div>
      ) : (
        <div onClick={handleChange}>
          <td className="row-word">{props.word}</td>
          <td className="row-word">{props.transcription}</td>
          <td className="row-word">{props.translation}</td>
        </div>
      )}
      <td>
        <div className="buttons">
          {pressed && <ButtonAdd />}
          <ButtonEdit />
          <ButtonDel />
        </div>
      </td>
    </tr>
  );
}

export default TableRow;
