import React, { useState } from 'react'

function ButtonTranslate(props) {
    const [pressed, setPressed] = useState(false)

    const handleChange = () => {
        setPressed(!pressed)
    }
    return !pressed ? (
        <button className="button" onClick={handleChange}>
            Проверить
        </button>
    ) : (
        `${props.translatin}`
    )
}

export default ButtonTranslate
