import React, {useState} from "react";
import "./Input.scss";
import ButtonDel from "../Buttons/ButtonDelete";
import ButtonAdd from "../Buttons/ButtonAdd";

function InputForWord(props) {
  const [imputData, setImputData]=useState({
word:"",
transcription:"",
translation:""
  });
  const[imputDirty, setImputDirty]=useState(false);
  const [error, setError]=useState("Поле не может быть пустым");

  const addImputData = event =>
  setImputData({
    ...imputData,
    [event.target.name]: event.target.value
  });

  const {word,transcription,translation} = imputData;

const validationField = value => !value ? "Please enter a value" : "";

const blurHandler=event=>{
switch (event.target.name) {
  case word:
    setImputDirty(true)
    break;
  case transcription:
    setImputDirty(true)
    break;
      case translation:
    setImputDirty(true)
    break;
}
}
  return (

      <tr className="rowAddWord">
      <td>
      {(imputDirty && error) && <div style={{color:"red"}}>{error}</div>}
    <input 
    className="addWord" 
    placeholder="Enter" 
    type="text"
    value={word} 
    onChange={e=>addImputData(e)} 
    name="word"
    onBlur={e=>blurHandler(e)}
      validator={validationField}
    required
    />
     </td>
          <td>
           {(imputDirty && error) && <div style={{color:"red"}}>{error}</div>}
    <input className="addWord" 
    placeholder="Enter" 
    type="text" 
    value={transcription} 
    onChange={e=>addImputData(e)} 
    name="transcription"
        onBlur={e=>blurHandler(e)}
          validator={validationField}
    required
    />
     </td>
          <td>
           {(imputDirty && error) && <div style={{color:"red"}}>{error}</div>}
   <input className="addWord" 
   placeholder="Enter"  
   type="text" 
   value={translation} 
   onChange={e=>addImputData(e)} 
   name="translation" 
       onBlur={e=>blurHandler(e)}
         validator={validationField}
   required
   />
     </td>
       <td>
        <div className="buttons">
        <ButtonAdd/>
        <ButtonDel />
        </div>
      </td>
        </tr>
  );
}

export default InputForWord;
