import React, {useState} from "react";
import "./Input.scss";
import ButtonDel from "../Buttons/ButtonDelete";
import ButtonAdd from "../Buttons/ButtonAdd";

function InputForWord(props) {
  const [imputData, setImputData]=useState({
word:"",
transcription:"",
translation:""
  });
  const [error, setError]=useState("Поле не может быть пустым")

  const addImputData = event =>{
setImputData({
    ...imputData,
    [event.target.name]: event.target.value
  });
  const {word,transcription,translation} = imputData;
  if(!event.target.value){
    setError("Поле не может быть пустым")
  }
  else{
      setError("")
  }
  }


  return (

      <tr className="rowAddWord">
      <td>
    <input 
    className="addWord" 
    placeholder="Enter" 
    type="text"
    value={word} 
    onChange={e=>addImputData(e)} 
    name="word"
    required
    />
     </td>
          <td>
    <input className="addWord" 
    placeholder="Enter" 
    type="text" 
    value={transcription} 
    onChange={e=>addImputData(e)} 
    name="transcription"
    required
    />
     </td>
          <td>
   <input className="addWord" 
   placeholder="Enter"  
   type="text" 
   value={translation} 
   onChange={e=>addImputData(e)} 
   name="translation" 
   required
   />
     </td>
       <td>
        <div className="buttons">
        <ButtonAdd/>
        <ButtonDel />
        </div>
      </td>
        </tr>
  );
}

export default InputForWord;
