import React, {useState} from "react";
import "./Input.scss";
import ButtonDel from "../Buttons/ButtonDelete";
import ButtonAdd from "../Buttons/ButtonAdd";

function InputForWord(props) {
  const [imputData, setImputData]=useState({
word:"",
transcription:"",
translation:""
  });

  handleChange = (event) =>
  setImputData({
    ...imputData,
    [event.target.name]: event.target.value
  });
  const {word,transcription,translation} = imputData;
const validationField = value => !value ? "Please enter a value" : "";

  return (
      <tr className="rowAddWord">
      <td>
    <input 
    className="addWord" 
    placeholder="Enter" 
    type="text" value={word} 
    onChange={ handleChange} 
    name={word}
   validator={validationField}

    />
     </td>
          <td>
    <input className="addWord" placeholder="Enter" type="text" value={transcription} onChange={  handleChange}name={transcription}/>
     </td>
          <td>
   <input className="addWord" placeholder="Enter"  type="text" value={translation} onChange={  handleChange} name={translation} />
     </td>
       <td>
        <div className="buttons">
        <ButtonAdd/>
        <ButtonDel />
        </div>
      </td>
        </tr>
  );
}

export default InputForWord;
