import React, { useState } from "react";
import ButtonDel from "../Buttons/ButtonDelete";
import ButtonAdd from "../Buttons/ButtonAdd";
import ButtonEdit from "../Buttons/ButtonEdit";
import "./TableRow.scss";

let words = [
  { id: "peace", english: "peace", transcription: "[ piːs ]", russian: "мир" },
  {
    id: "friendship",
    english: "friendship",
    transcription: "[ ˈfrendʃɪp ]",
    russian: "дружба",
  },
  {
    id: "friendship",
    english: "friendship",
    transcription: "[ ˈfrendʃɪp ]",
    russian: "дружба",
  },
  { id: "gum", english: "gum", transcription: "[ ɡʌm ]", russian: "жвачка" },
];

function TableRow() {
  const [pressed, setPressed] = useState(false);
  const handleChange = () => {
    setPressed(!pressed);
  };

  return words.map((word, i) => (
    <tr key={word.id}>
      {pressed ? (
        <div onClick={handleChange}>
          <td>
            <input value={word.english} />
          </td>
          <td>
            <input value={word.transcription} />
          </td>
          <td>
            <input value={word.russian} />
          </td>
        </div>
      ) : (
        <div onClick={handleChange}>
          <td>{word.english}</td>
          <td>{word.transcription}</td>
          <td>{word.russian}</td>
        </div>
      )}
      <td>
        <div className="buttons">
          {i === 0 && <ButtonAdd />}
          <ButtonEdit />
          <ButtonDel />
        </div>
      </td>
    </tr>
  ));
}

export default TableRow;
